import {
  Alert,
  Button,
  Grid,
  InputAdornment,
  Snackbar,
  TextField,
} from "@mui/material";
import { Formik } from "formik";
import React, { useState } from "react";
import { AccountCircle, Add, CakeOutlined, Email, Phone, TranslateOutlined } from "@mui/icons-material";
import * as yup from "yup";
import API from "../utils/axios";
import PlacesAutocomplete from "react-places-autocomplete";
import {
  geocodeByAddress,
  geocodeByPlaceId,
  getLatLng,
} from "react-places-autocomplete";

const initialValues = {
  firstName: "",
  lastName: "",
  dob: "",
  language: "",
  phone: "",
  email: "",
  address: "",
  notes: "",
};

const style = {
  textFiedStyle: {
  marginTop: '15px'
}}
export default function Form() {
  const colors = ["#25A575", "#25A575", "#2595A5", "#3A719B", "#142B58"];
  const validationSchema = yup.object({
    firstName: yup.string().required("first name is required"),
    lastName: yup.string().required("last name is required"),
    dob: yup.string().required("date of birth is required"),
    language: yup.string().required("contact language is required"),
    phone: yup
      .number()
      .moreThan(999999999, "10 digits required")
      .typeError("numbers are allowed")
      .required("Mobile is required"),
    email: yup
      .string()
      .email("Enter a valid email")
      .required("email is required"),
    address: yup.string().max(100).required("address is required"),
    notes: yup.string().max(100),
  });
  const [referrals, setReferrals] = useState([]);
  const [type, setType] = useState("text");
  const [open, setOpen] = useState(false);
  const [message, setMessage] = useState("");
  const [alertColor, setAlertColor] = useState("success");
  const [address, setAddress] = useState("");

  const sendReferrals = () => {
    if (referrals && referrals.length !== 0) {
      API.post(`/patient`, referrals)
        .then((response) => {
          const { status, message } = response.data;
          if (status) {
            setAlertColor("success");
            setMessage(
              `Success! You have submitted ${referrals.length} pending requests. You will be notified once they've been approved`
            );
            setOpen(true);
          } else {
            setMessage(message);
            setAlertColor("error");
            setOpen(true);
          }
        })
        .catch((error) => {
          setMessage(error.message);
          setAlertColor("error");
          setOpen(true);
        });
    }
  };
  const handleClose = () => {
    setOpen(false);
  };

  const handleChanger = (address) => {
    setAddress(address);
  };

  const handleSelect = (address) => {
    geocodeByAddress(address)
      .then((results) => getLatLng(results[0]))
      .then((latLng) => console.log("Success", latLng))
      .catch((error) => console.error("Error", error));
  };

  return (
    <>
      {referrals &&
        referrals.map((row, i) => {
          return (
            <Grid style={{ marginBottom: "10px" }} key={i}>
              <Grid style={{ backgroundColor: "white" }}>
                <Grid container>
                  <Grid item xs={12}>
                    <Grid container style={{ height: "5vh" }}>
                      <Grid
                        item
                        xs={1}
                        style={{
                          backgroundColor: colors[i],
                          paddingTop: "10px",
                        }}
                      >
                        {i + 1}
                      </Grid>
                      <Grid
                        item
                        xs={11}
                        align="left"
                        style={{ paddingTop: "10px", paddingLeft: "5px" }}
                      >
                        {row.firstName + "" + row.lastName}
                      </Grid>
                    </Grid>
                  </Grid>
                </Grid>
              </Grid>
            </Grid>
          );
        })}
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={(values, actions) => {
          setReferrals((oldData) => [...oldData, values]);
          actions.resetForm({
            firstName: "",
            lastName: "",
            dob: "",
            language: "",
            phone: "",
            email: "",
